function loop(){ctx.clearRect(0,0,canvas.width,canvas.height),step++;for(var a=lines.length-1;a>=0;a--){ctx.fillStyle=lines[a];var t=(step+20*a)*Math.PI/140,e=30*Math.sin(t),n=20*Math.cos(t);ctx.beginPath(),ctx.moveTo(0,canvas.height/2+e),ctx.bezierCurveTo(canvas.width/2,canvas.height/2+e-50,canvas.width/2,canvas.height/2+n-50,canvas.width,canvas.height/2+n),ctx.lineTo(canvas.width,canvas.height),ctx.lineTo(0,canvas.height),ctx.lineTo(0,canvas.height/2+e),ctx.closePath(),ctx.fill()}requestAnimFrame(loop)}var canvas=document.getElementById("canvas"),ctx=canvas.getContext("2d");canvas.width=canvas.parentNode.offsetWidth,canvas.height=canvas.parentNode.offsetHeight,window.requestAnimFrame=window.requestAnimationFrame||window.webkitRequestAnimationFrame||window.mozRequestAnimationFrame||function(a){window.setTimeout(a,1e3/60)};var step=0,lines=["rgba(255,255,255, 1)","rgba(255,255,255, 0.5)","rgba(255,255,255, 0.2)"];loop();