function randomDistance(t,a){return Math.floor(Math.random()*(t-a+1)*10+a)}for(var stars=document.getElementById("stars"),star=document.getElementsByClassName("star"),j=0;j<30;j++){var newStar=document.createElement("div");newStar.className="star",newStar.style.top=randomDistance(30,-30)+"px",newStar.style.left=randomDistance(150,20)+"px",stars.appendChild(newStar)}for(var i=0,len=star.length;i<len;i++)star[i].style.animationDelay=i%6==0?"0s":.8*i+"s";