function checkShow(t){var o=$(window).scrollTop(),n=$(window).height(),c=t.offset().top;return c<o+n&&c>o}function isLoaded(t){return t.attr("data-src")===t.attr("src")}function loadImg(t){t.attr("src",t.attr("data-src"))}$(window).on("scroll",function(){$(".container img").each(function(){checkShow($(this))&&!isLoaded($(this))&&loadImg($(this))})});